<?php include('../include/head.php'); ?><?php include('../include/menu.php'); ?><br><br><br><div class="container">    <?php    if (empty($_SESSION['id'])) //les membres connectes ne peuvent pas s'inscrire    {        /* il faut que toutes les variables du formulaire existent*/        if(isset($_POST['identifiant']) && isset($_POST['pass']))        {            /*il faut que tous les champs soient renseignes*/            if($_POST['identifiant']!="" && $_POST['pass']!="")            {                /*on crypte le mot de passe pour faire le test*/                $passhache = sha1($_POST['pass']);                require '../include/connectbdd.php';                /* on verifie qu'un membre a bien cet email et ce mot de passe*/                $req = $bdd->prepare('SELECT * FROM membre WHERE identifiant = :identifiant AND pass= :pass ');                $req->execute(array('identifiant'=> $_POST['identifiant'], 'pass'=> $passhache));                $resultat=$req->fetch();                /*s'il n'y a pas de resultat, on renvoie a la page de connexion*/                if(!$resultat)                {                    ?>                    <div class="alert alert-danger">                        <strong>Erreur :</strong> Une erreur s'est produite !                    </div>                    <?php                }                else                {                    /* on cree les variables de session du membre qui lui serviront pendant sa session*/                    $_SESSION['id']= $resultat['id'];                    $_SESSION['identifiant']= $resultat['identifiant'];                    $_SESSION['email']= $resultat['email'];                    $_SESSION['prenom']= $resultat['prenom'];                    $_SESSION['nom']= $resultat['nom'];                    $_SESSION['adresse']= $resultat['adresse'];                    $_SESSION['sexe']= $resultat['sexe'];                    $_SESSION['date_naissance']= $resultat['date_naissance'];                    $_SESSION['level']= $resultat['level'];                    $_SESSION['ban']= $resultat['ban'];                    $_SESSION['ip']= $resultat['ip'];                    if ($_SESSION['ban'] == 1) {                        session_destroy();                        ?>                        <div class="alert alert-danger">                            <strong>Erreur :</strong> Vous avez été banni de ce site. Ceci veut dire que vous n'avez plus accès à votre compte à cause d'un non respect du règlement.                        </div>                            <script type="text/javascript">                                <!--                                var redirection = 'window.location.replace("logout.php");';                                setTimeout(redirection,8000);                                // -->                            </script>                        <?php                    } else {                        ?>                            <script type="text/javascript">                                <!--                                var redirection = 'window.location.replace("index.php");';                                setTimeout(redirection,800);                                // -->                            </script>                            <div class="alert alert-success">                                <strong>Suucès :</strong> Vous êtes à présent connecté !                            </div>                        <?php                        }                        $req->closeCursor();                }            }            else            {                ?>                <script type="text/javascript">                    <!--                    var redirection = 'window.location.replace("login.php");';                    setTimeout(redirection,4000);                    // -->                </script>                <div class="alert alert-danger">                    <strong>Erreur :</strong> Il faut remplir tous les champs !                </div>                <?php            }        }        else        {            ?>            <script type="text/javascript">                <!--                var redirection = 'window.location.replace("login.php");';                setTimeout(redirection,4000);                // -->            </script>            <div class="alert alert-danger">                <strong>Erreur :</strong> Une erreur s'est produite !            </div>            <?php        }    } else {        ?>        <div class="alert alert-danger">            <strong>Erreur :</strong> Vous êtes déjà connecté !        </div>        <?php    } ?></div> <!-- /container -->